string fixint(int jumlah) {
	string result = to_string(jumlah);
	for (int i = result.size() - 3; i > 0; i -= 3)
		result.insert(i, ",");
	return result;
}
inline string get_string_low(string tt)
{
	string result;
	for (char c : tt) {
		result += tolower(c);
	}
	return result;
}
