			vector<int> update_tiles = locked_tiles_around_lock[i_];
			int x = update_tiles[0];
			int y = update_tiles[1];
			int sq_ = x + (y * 100);
			memcpy(blc + (i_ * 2), &sq_, 2);
			shadow_copy_2[x + (y * 100)].scanned = false;
