					spawnas_x = i_ % xSize, spawnas_y = i_ / xSize;
					pInfo(peer)->c_x = spawnas_x, pInfo(peer)->c_y = spawnas_y;
					gamepacket_t p(delay, pInfo(peer)->netID);
