void down_save() {
	hoshi_db_logs("Saving Players & Worlds");
	for (ENetPeer* currentPeer = server->peers; currentPeer < &server->peers[server->peerCount]; ++currentPeer) {
		if (currentPeer->state != ENET_PEER_STATE_CONNECTED or currentPeer->data == NULL) continue;
		save_player(pInfo(currentPeer), false);
	}
	Sleep(1000);
	for (int i = 0; i < static_cast<int>(worlds.size()); i++) {
		string world_name = worlds[i].name;
		save_world(world_name, false);
	}
	hoshi_info("Successfully Saved all Worlds and Players");
	hoshi_db_logs(to_string(worlds.size()) + " Worlds Saved");
	save_guilds();
	Sleep(1000);
	enet_host_flush(server);
	exit(0);
}
void save_all() {
	for (ENetPeer* currentPeer = server->peers; currentPeer < &server->peers[server->peerCount]; ++currentPeer) {
		if (currentPeer->state != ENET_PEER_STATE_CONNECTED or currentPeer->data == NULL) continue;
		save_player(pInfo(currentPeer), false);
	}
	for (int i = 0; i < static_cast<int>(worlds.size()); i++) {
		string world_name = worlds[i].name;
		save_world(world_name, false);
	}
	save_guilds();
	Sleep(1000);
}

